import React from 'react';

interface CarrouselProps {
    items: JSX.Element[];
}

interface CarrouselState {
    curentPictureIndex: number;
}

export class Carrousel extends React.Component<CarrouselProps, CarrouselState> {
    public constructor(props: CarrouselProps) {
        super(props);
        this.state = {
            curentPictureIndex: 0
        };
    }
    public render() {
        const style: React.CSSProperties = {
            //
        };
        return <div>
            <button onClick={() => this._goLeft()}>Go left</button>
            {this.props.items[this.state.curentPictureIndex]}
            <button onClick={() => this._goRight()}>Go right</button>
        </div>;
    }
    private _goLeft() {
        var maxIndex = this.props.items.length - 1;
        if (this.state.curentPictureIndex === 0) {
            this.setState({ curentPictureIndex: maxIndex });
        } else {
            this.setState({
                curentPictureIndex: this.state.curentPictureIndex -1
            });
        }
    }
    private _goRight() {
        var maxIndex = this.props.items.length - 1;
        if (this.state.curentPictureIndex === maxIndex) {
            this.setState({ curentPictureIndex: 0 });
        } else {
            this.setState({
                curentPictureIndex: this.state.curentPictureIndex + 1
            });
        }
    }
}
