import * as React from "react";
import { Listview } from "../components/listview/listview";
import { Link } from "react-router-dom";
import { getAuthToken } from "../components/with_auth/with_auth";



// component should show only 1 link with its replies
// GET LINK BY ID


interface LinkItem {
    user: {
        name: string;
    },
    reply: {
        id: number,
        text: string;
        userId: number;
        date: string;
    },
    id: number;
    title: string;
    question: string;
    field: string;
    date: string;
}

interface LinkProps {
    token: string | null;
}

interface LinkState {
    link: {},
    replies: LinkItem[] | null
}

export class LinkById extends React.Component<LinkProps, LinkState> {
    public constructor(props: LinkProps) {
        super(props);
        this.state = {
            link: this.props.children.params.id,
            replies: null
        };
    }
    public componentDidMount() {
        (async () => {
            const data = await getData();
            this.setState({ replies: data });
        })();
    }

    public render() {
            return <div>
                {this._renderPrivate()}
            </div>;
    }

    private _renderPrivate() {
        const token = getAuthToken();
        if(token){
            if (this.state.replies === null) {
                return <div>Loading...</div>;
            } else {
                return <div>
                    <Listview
                        items={
                            this.state.replies.map((replies) => {
                                // count how many replies in each link
                                let comments = Object.keys(replies.comments).length;
                                let c = (comments <= 1) ? "comment" : "comments";
                                return <div>

                                    <h6>{"Posted by "}{replies.user.name}    {"----"}      {replies.date}</h6>
                                    <h4>{replies.text}  </h4>
                                    <h5> {comments} {c}</h5>
                                </div>; 
                            })
                        }
                    />
                </div>;
            }
        }else{
            return <div>
                <h3>{ "ERROR, You must be logged in to see this link"}</h3>
            </div>
        }
    }
}

async function getData() {
    const response = await fetch("/links/:id");
    const json = await response.json();
    return json as RepliesItem[];
}
