import React from 'react';


interface SearchProps {
    searchBoxName: string;
}

interface SearchState {
    term: string;
}

export class SearchBar extends React.Component<SearchProps, SearchState> {

  public constructor(props: SearchProps){
    super(props);
    this.state={term:''};
  }


    public render() {
      const name = this.props.searchBoxName || undefined
        return (
            <div className="search-box">
              <input name={name} className="search-input" id="search" type="text" placeholder="Search" value={this.state.term}/>
            </div>
        );
    }
}

export default SearchBar;
